import React, { useEffect, useState } from 'react'

const FoodItems = ({ dishes }) => {
    const [counter,setCounter] = useState(0)
    useEffect(() => {
        console.log("dishes", dishes)

    }, [])

    return (
        <div className=''>
            {dishes ? dishes.map((result) => {
                return (
                    <>
                        <div className='border h-fit flex justify-between'>
                            <div className='ml-5 pt-2 text-left'>
                                <label c htmlFor="">{result.dish_name}</label><br />
                                <div className='text-left'>
                                    <label htmlFor="">{result.dish_currency} {result.dish_price}</label><br />
                                    <label htmlFor="">{result.dish_description}</label>
                                    <div className='my-2'>
                                        <button onClick={()=>setCounter(counter===0?counter=0:counter-1)} className='w-10 bg-green-600 px-6 h-10 text-white rounded-l-full'>-</button>
                                        <button className='w-10 bg-green-600 text-white h-10'>{counter}</button>
                                        <button onClick={()=>setCounter(counter+1)} className='w-10 bg-green-600 text-white px-6 h-10  rounded-r-full'>+</button>
                                    </div>
                                    <span>custom</span>
                                </div>
                            </div>
                            <span>calories</span>
                            <div className='h-40 m-5 w-40 mr-5 rounded-md overflow-hidden' style={{ backgroundImage: "url(" + result.dish_image + ")", backgroundSize: "cover" }}>
                            </div>
                        </div>
                    </>
                )
            }) : null}
        </div>
    )
}

export default FoodItems
