                                        <button className='w-10 bg-green-600 text-white h-10'>{0}</button>
                                  }

                                        <button onClick={add.bind(this,index)} className='w-10 bg-green-600 text-white px-6 h-10 pr-7 rounded-r-full'>+</button>

