import React, { useEffect, useState } from 'react'
import { motion ,AnimatePresence} from 'framer-motion';


const FoodItems = ({ dishes }) => {
    const [counter,setCounter] = useState(0)
    useEffect(() => {
        console.log("dishes", dishes)

    }, [dishes])

    return (
        <AnimatePresence>


        <motion.div className=''
        initial={{ x: "100%" }}
        animate={{ x: "calc(100vw -50%" }}
        exit={{ x: "calc(100vw - 10%"}}
        >
            {dishes ? dishes.map((result) => {
                return (
                    <>
                        <div className='border h-fit flex justify-between'>
                            <div className='ml-5 pt-2 text-left'>
                                <label className='font-bold' htmlFor="">{result.dish_name}</label><br />
                                <div className='text-left'>
                                    <label htmlFor="">{result.dish_currency} {result.dish_price}</label><br />
                                    <label className='text-slate-500' htmlFor="">{result.dish_description}</label>
                                    <div className='my-2'>
                                        <button onClick={()=>setCounter(counter===0?counter=0:counter-1)} className='w-10 bg-green-600 px-6 h-10 text-white rounded-l-full'>-</button>
                                        <button className='w-10 bg-green-600 text-white h-10'>{counter}</button>
                                        <button onClick={()=>setCounter(counter+1)} className='w-10 bg-green-600 text-white px-6 h-10 pr-7 rounded-r-full'>+</button>
                                    </div>
                                    <span>custom</span>
                                </div>
                            </div>
                            <span>calories</span>
                            <div className='h-40 m-5 w-40 mr-5 rounded-md overflow-hidden' style={{ backgroundImage: "url(" + result.dish_image + ")", backgroundSize: "cover" }}>
                            </div>
                        </div>
                    </>
                )
            }) : null}
        </motion.div>
        </AnimatePresence>
    )
}

export default FoodItems
