import React, { useState } from 'react'
import FoodItems from './FoodItems'
import "./categories.css";

const Categories = ({details}) => {
    const[style,setStyle] = useState('w-96 border-b pb-1')
    const [on,setOn] = useState(false)
    const style1 = 'w-96 border-b pb-1'
    const style2 = 'w-96 border-b pb-1 border-red-500'
    const one = () =>{
        setOn(!on)
      if(on){
        setStyle(style2)
      }else{
        setStyle(style1)
      }
    }

    console.log(details)

  return (

    <div>
        <div className='flex jusitfy-center w-screen border-b overflow-x-scroll scroll' >
            <div className=' flex mx-auto po'>
           {details?details.map((result)=>{
            return(
                <>
                 <div onClick={one} className={style}>{result.menu_category}</div>
                </>
            )
           }):null}
            </div>
        </div>
        <div>
            <FoodItems/>
        </div>
    </div>
  )
}

export default Categories
