    // console.log(dishes)
    return (

        <div>
            <div className='flex jusitfy-center w-screen border-b overflow-x-scroll scroll' >
                <div className=' flex mx-auto cursor-pointer'>
                    {details ? details.map((result) => {
                        return (
                            <>
                                <div className={style} onClick={()=>setStyle('w-96 border-b pb-1 border-red-500')}>

                                    <div onClick={() => { setDishes(result.category_dishes) }}>{result.menu_category}</div>

                                </div>
                            </>
                        )
                    }) : null}
                </div>
            </div>
            <AnimatePresence>

                <motion.div
                    initial={{ x: 300, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    exit={{ x: -300, opacity: 0 }}>
                    <FoodItems dishes={dishes} />
                </motion.div>
            </AnimatePresence>
        </div>
    )
}

export default Categories
