import React, { useEffect, useState } from 'react'
import FoodItems from './FoodItems'
import "./categories.css";
import { AnimatePresence, motion } from 'framer-motion';
const Categories = ({ details }) => {
    const [style, setStyle] = useState('w-96 border-b pb-1')
    const [selected, setSelected] = useState()
    const [dishes, setDishes] = useState([])
    console.log(selected)

    useEffect(() => {
        details.map((res) => { return setDishes(res.category_dishes) })

    }, [details])



    return (

        <div>
            <div className='flex jusitfy-center w-screen border-b overflow-x-scroll scroll' >
                <div className=' flex mx-auto cursor-pointer'>
                    {details ? details.map((result) => {
                        return (
                            <>
                                    {selected === result.menu_category? setStyle("bg-red-500"):setStyle("bg-blue-500")}
                                <div className='border-red-500' onClick={() => setSelected(result.menu_category)}>
                                    <div onClick={() => { setDishes(result.category_dishes) }} className={style}>{result.menu_category}</div>

                                </div>
                            </>
                        )
                    }) : null}
                </div>
            </div>
            <AnimatePresence>

                <motion.div
                    initial={{ x: 300, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    exit={{ x: -300, opacity: 0 }}>
                    <FoodItems dishes={dishes} />
                </motion.div>
            </AnimatePresence>
        </div>
    )
}

export default Categories
